import React, { Component } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import * as AuthAction from '../../actions/userActions';
import RegisterForm from './RegisterForm';
class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      email: '',
      password: '',
      isUsernameCorrect:true,
      isPassword:true
    }

  }
  handleChange = (name, value) => {
    this.setState({ [name]: value })
  }
  onValidation= (username,email,password)=>{
    let usernamePatt= /^[\w]{4,20}$/;
    if(password.length >= 6 && usernamePatt.test(username)){
       this.setState({isPasswordCorrect:true})
       this.setState({isUsernameCorrect:true})

       this.props.actions.register(username, password, email)

    }
    if(password.length<6){
      console.log('NOT CORRECT')
      this.setState({isPasswordCorrect:false})
    }else {
      this.setState({isPasswordCorrect:true})
    }
    if(!usernamePatt.test(username)){
      this.setState({isUsernameCorrect:false})
    }else{
      this.setState({isUsernameCorrect:true})
    }
  }
  handleSubmit = async (values) => {
    const { username, password, email } = values;
   await this.onValidation(username,email,password)
  }
  render() {
    const { registerFailed } = this.props;
    return (
      <div>


        <RegisterForm
          isPasswordCorrect={isPasswordCorrect}
          isUsernameCorrect={isUsernameCorrect}
          isRegisterFailed={registerFailed}
          onSubmit={(values) => {
            this.handleSubmit(values)
          }}

        />

      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  registerFailed: state.authentication
})

const mapDispatchToProps = dispatch => ({
  actions: bindActionCreators(AuthAction, dispatch)
})

export default connect(mapStateToProps, mapDispatchToProps)(Register)
