import React, { Component } from 'react';
import Dropzone from 'react-dropzone';
import request from 'superagent';

const CLOUDINARY_UPLOAD_PRESET = 'mockfinal19'
const CLOUDINARY_UPLOAD_URL = 'https://api.cloudinary.com/v1_1/dv7rbbhuf/upload'


export default class DropzoneImg extends Component {
    constructor(props) {
        super(props)

        this.state = {
            uploadedFileCloudinaryUrl: '',
            url:''
        };
    }
    onHandleChange=(e)=>{
      
    }
    onImageDrop(files) {
        this.setState({
            uploadedFile: files[0]
        })
        this.handleImageUpload(files[0])
    }
    handleImageUpload(file) {
        let upload = request.post(CLOUDINARY_UPLOAD_URL)
            .field('upload_preset', CLOUDINARY_UPLOAD_PRESET)
            .field('file',file);
        upload.end((err,response)=>{
            if(err){
                console.error(err);
            }
            if(response.body.secure_url !== ''){
                this.setState({
                    uploadedFileCloudinaryUrl: response.body.secure_url
                })
                this.props.onGetAvatar(uploadedFileCloudinaryUrl);
            }
        })

    }
    render() {
        return (
            <div>
                <div className="FileUpload" style={{ height:'4em',
                background: '#ccc', textAlign:'center'
                }}>
                <Dropzone 
                    multiple={false}
                    accept="image/*"
                    onDrop={this.onImageDrop.bind(this)}>
                    {({ getRootProps, getInputProps }) => {
                        return (
                            <div {...getRootProps()} >

                                <input {...getInputProps()} />
                                {
                                    <p > Kéo ảnh hoặc click để chọn ảnh </p>
                                }
                            </div>
                        )
                    }}

                </Dropzone>
                </div>
                <div>
                    {this.state.uploadedFileCloudinaryUrl==='' ? null :
                        <div>
                            <p>{this.state.uploadedFile.name}</p>
                            <img src={this.state.uploadedFileCloudinaryUrl}/>
                        </div>
                
                    }

                </div>
            </div>
        )
    }
}
