import * as types from './../Config/actionConstant';


var initalState = [];
var findIndex = (state, id) => {
    var result = -1;
    state.forEach((state, index) => {
        if (state.id.newsId === id) {
            result = index;
        }
    });
    console.log(result);
    return result;
}
const hello = (state = initalState, action) => {
    switch (action.type) {
        case types.GET_NEWS:
            state = action.news;
            console.log(state);
            return [...state];
        case types.DELETE_NEW:
            var id = action.id;
            console.log('1'+id)
            var index = findIndex(state, id);
            console.log(state)
            state.splice(index,1);
            return [...state]

        default:
            return [...state];

    }

};
export default hello;
