import axios from 'axios';
import * as addr from './../Config/ConFigAPI';
export default function callAPI(endpoint,method='GET',body) {
    return axios({
        method:method,
        url:`${addr.URL_API}/${endpoint}`,
        data:body
    }).catch(err=>{
        console.log(err);

    });
}
