import React, { Component } from 'react';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import * as AuthAction from '../../actions/userActions';
import LoginForm from './LoginForm';
class Login extends Component {
  constructor(props) {
    super(props)
    this.state = {
      username: '',
      password: '',
      isEmailCorrect:false,
      isPasswordCorrect:false
    }
  }

  onValidation=(username,password)=>{
    let emailPatt =/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i;
    if(username.includes("@")){
      let isEmailCorrect= emailPatt.test(username);
      if(isEmailCorrect){
        this.setState({isEmailCorrect:true})

      }else{
        this.setState({isEmailCorrect:false})
        console.log(isEmailCorrect)
      }
    }else{
      this.setState({isEmailCorrect:true})
    }

    if(password.length>5 && password.length<21){
      this.setState({isPasswordCorrect:true}, _ => {
        this.props.actions.login(username, password)
      })
    }else{
      this.setState({isPasswordCorrect:false})
    }
  }
  handleChange = (name, value) => {
    this.setState({ [name]: value })
  }

  handleSubmit = async (values) => {
    const {isEmailCorrect,isPasswordCorrect}=this.state;
    const { username, password } = values

   await this.onValidation(username,password)
    console.log('emaim state', isEmailCorrect)
    console.log('passw state', isPasswordCorrect)


    // if(isEmailCorrect && isPasswordCorrect){
    //   console.log('IS CORRECT')
    // this.props.actions.login(username, password)
    // }else{
    //   console.log('NOT CORRECT')
    //   if(!isEmailCorrect && !isPasswordCorrect){
    //     alert('Password and email is invalid')
    //     this.setState({isPasswordCorrect:true})

    //   }else if(!isEmailCorrect){
    //     alert('Email is invalid')

    //   }else if(!isPasswordCorrect){
    //     alert('Password 6-20 characters')
    //     this.setState({isPasswordCorrect:true})
    //   }
    // }
  }

  render() {
    const {  loginFailed  } = this.props

    return (
      <div className="form">

          <LoginForm

            isLoginFailed={loginFailed}
            onSubmit={(values) => {
              this.handleSubmit(values)
            }}
          />
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  loginFailed: state.authentication
})

const mapDispatchToProps = dispatch => ({
  actions: bindActionCreators(AuthAction, dispatch)
})

export default connect(mapStateToProps, mapDispatchToProps)(Login)
