import * as Types from '../Config/managerConstant';
import API from '../utils/api';
//import API from '../utils/api';
{/* luu data tu sever ve stogre*/ }

export const saveNewsToStore=(news)=>{
    return{
        type:Types.GET_NEWS,
        news:news
    }
}
export const saveNewsRequest=()=>{
    return (dispath)=>{
        return API.get('/new').then(reps=>{

            dispath(saveNewsToStore(reps.data));
        });
    };
}

{/* xoa data tren sever sau do tren stogre*/ }

export const deleteNewStore = (id) => {
    return {
        type: Types.DELETE_NEW,
        id: id
    }
}
export const deleteNewRequest = (id) => {
    return (dispath) => {
        return API.delete(`new/${id}`).then(reps => {
            dispath(deleteNewStore(id))
        });
    };
}
export const addNewsRequest=(news)=>{
    return(dispath)=>{
        return API.post('new', news).then(reps => {
            console.log(reps);
            if(reps.status===200)
            {  
                alert('Them thanh cong');
            }           
        });
    };
}
export const crawlerNews= ()=> async dispacth=>{
 const searchURL= `https://vnexpress.net/thoi-su`;
 const response = await fetch(searchURL);
 const htmlString = await response.text();
 dispacth({type:Types.CRAWLER,htmlString})
}
