import React, { Component } from 'react'
import TableRow from './TableRow'
import { connect } from 'react-redux'
import '../../styles/css/main.css';
import { Link, Router } from 'react-router-dom';
import Pagination from '../../pagination/Pagination'
import { actFetchUsersRequest, actDeleteUsersRequest } from '../../actions/managerAction';
export class ManagerUser extends Component {
  constructor(props) {
    super(props)

    this.state = {
      currentPage: 1
    }
  }
  componentDidMount(numPage) {
    this.props.fetchAllUsers(0,5)
  }
  onDelete = (id) => {
    this.props.onDeleteUsers(id);
  }
  changeCurrentPage = async (numPage) => {
    await this.setState({ currentPage: numPage });
    await this.props.fetchAllUsers(numPage-1,5);
  }

  render() {
    
    return (
      <div>
        <div className="app-title">
          <div>
            <h1><i className="fa fa-user"></i>&nbsp; Quản lí người dùng</h1>
            <p></p>
          </div>
          <ul className="app-breadcrumb breadcrumb">
            <li className="breadcrumb-item"><i className="fa fa-home fa-lg"></i></li>
            <li className="breadcrumb-item"><a href="#">User</a></li>
          </ul>
        </div>

        <div className="row">
          <div className="col-lg-7">
            <p className="bs-component ">
              <a href="adduser.html">
                <Link className="btn btn-primary btn-right" to="/manager/user/add">+ Thêm</Link>
              </a>
            </p>
          </div>
          <div className="col-md-12">
            <div className="tile">
              <div className="tile-body">
                <table className="table table-hover table-bordered" id="sampleTable">
                  <thead>
                    <tr>
                      <th>STT</th>
                      <th>Email</th>
                      <th>Tên đầy đủ</th>
                      <th>Ngày sinh</th>
                      <th>Địa chỉ</th>
                      <th>ID</th>
                      <th>Thực hiện</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.tabRow(this.props.users)}
                  </tbody>
                  <Pagination
                    currentPage={this.state.currentPage}
                    totalPages={this.props.totalPages}
                    changeCurrentPage={this.changeCurrentPage}
                    theme="default"
                  />
                </table>

              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
  tabRow(users) {
    return users.map((object, i) => {
      return <TableRow obj={object} key={i} index={i} onDelete={this.onDelete} />
    })
  }
}

const mapStateToProps = state => {

  return {
    users: state.users.users,
    totalPages: state.users.totalPages
  }

}

const mapDispatchToProps = dispatch => {
  return {
    fetchAllUsers: (currentPage, size) => {
      dispatch(actFetchUsersRequest(currentPage, size))
    },
    onDeleteUsers: (id) => {
      dispatch(actDeleteUsersRequest(id))
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ManagerUser)
