import * as Types from '../Config/managerConstant';
import API from '../utils/api';


//fetch User
export const actFetchUsersRequest=(currentPage,size)=>{
    return (dispatch)=>{
        return API.get(`/userany/all/page=${currentPage}&size=${size}`).then(res=>{
            dispatch(actFetchUsers(res.data.content))
        })
    }
}

export const actFetchUsers= (users)=>{
    return{
        type:Types.FETCH_USERS,
        users
    }
}
//Delete user
export const actDeleteUsersRequest=(id)=>{
    return (dispatch)=>{
        return API.delete(`/users/${id}`).then(res=>{
            dispatch(actDeleteUsers(id))
        })
    }
}
export const actDeleteUsers=(id)=>{
    return{
        type:Types.DELETE_USER,
        id
    }
}
//Add new user
export const actAddUserRequest=(user)=>{
    return dispatch=>{
        return API.post('/users/',user).then(res=>{
            dispatch(actAddUser(res.data))
        })
    }
}
export const actAddUser=(user)=>{
    return {
        type: Types.ADD_USER,
        user
    }
}
//get user in create table
export const actGetUserRequest=(id)=>{
    return dispatch=>{
        return API.get(`/users/${id}`).then(res=>{
            dispatch(actGetUser(res.data))
        })
    }
}

export const actGetUser=(user)=>{
    return {
        type: Types.EDIT_USER,
        user
    }
}
//update user
export const actUpdateUserRequest=(user)=>{
    return dispatch=>{
        return API.put(`/users/${user.id}`,user).then(res=>{
            dispatch(actUpdateUser(res.data))
        })
    }
}
export const actUpdateUser=(user)=>{
    return {
        type:Types.UPDATE_USER,
        user
    }
}
