import React, { Component } from 'react';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux';
import * as AuthAction from '../../actions/userActions';
import LoginForm from './LoginForm';
class Login extends Component {
  constructor(props) {
    super(props)
    this.state = {
      username: '',
      password: '',
      isEmailCorrect:false,
      isPasswordCorrect:false
    }
  }

  onValidation= async(username,password)=>{
    let usernamePatt= /^[\w]{4,20}$/;
    if(password.length >= 6 && usernamePatt.test(username)){
      await  this.setState({isPasswordCorrect:true})
     await  this.setState({isEmailCorrect:true})

        this.props.actions.login(username, password)

    }else{
      console.log('NOT CORRECT')
      this.setState({isPasswordCorrect:false})
      this.setState({isEmailCorrect:false})
    }
  }
  handleChange = (name, value) => {
    this.setState({ [name]: value })
  }

  handleSubmit = values => {
    const { username, password } = values

    this.onValidation(username,password)
    console.log(this.state)
    // if(isEmailCorrect && isPasswordCorrect){
    //   console.log('IS CORRECT')
    // this.props.actions.login(username, password)
    // }else{
    //   console.log('NOT CORRECT')
    //   if(!isEmailCorrect && !isPasswordCorrect){
    //     alert('Password and email is invalid')
    //     this.setState({isPasswordCorrect:true})

    //   }else if(!isEmailCorrect){
    //     alert('Email is invalid')

    //   }else if(!isPasswordCorrect){
    //     alert('Password 6-20 characters')
    //     this.setState({isPasswordCorrect:true})
    //   }
    // }
  }

  render() {
    const {  loginFailed  } = this.props

    return (
      <div className="form">

          <LoginForm

            isLoginFailed={loginFailed}
            onSubmit={(values) => {
              this.handleSubmit(values)
            }}
          />
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  loginFailed: state.authentication
})

const mapDispatchToProps = dispatch => ({
  actions: bindActionCreators(AuthAction, dispatch)
})

export default connect(mapStateToProps, mapDispatchToProps)(Login)
