import React, { Component } from 'react'
import axios from 'axios';
import * as AuthAction from '../../actions/userActions';
import { connect } from 'react-redux'
import { bindActionCreators } from 'C:/Users/Admin/AppData/Local/Microsoft/TypeScript/3.3/node_modules/redux';

class Manager extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
          users:[],
         userId:'',
         userEmail:'',
         userPassword:'',
         userFirstname:'',
         userLastname:'',
         userMobile:'',
         userRole:'',
         userStatus:'',
      }
    }
    componentDidMount(){
        axios.get('http://5c7cd0f5dd19010014c8e92c.mockapi.io/api/v1/users')
        .then(response=>{
            this.setState({users: response.data})
        })
        console.log(response.data)
    }
    tabRow(){
        return this.state.users.map(function(obj){
            return (
                <div>
                    <tr>
                        <td>{this.props.obj.userId}</td>
                        <td>{this.props.obj.userEmail}</td>
                        <td>{this.props.obj.userPassword}</td>
                        <td>{this.props.obj.userFirstname}</td>
                        <td>{this.props.obj.userLastname}</td>
                        <td>{this.props.obj.userMobile}</td>
                        <td>{this.props.obj.userRole}</td>
                        <td>{this.props.obj.userStatus}</td>
                        <td>Sua</td>
                        <td>Xoa</td>
                    </tr>
                </div>
            )
        });
    }
  render() {
    return (
        <div className="managerContainer">
        <div>
            {/*user data */}
            <table className="tableContainer">
                <tbody>
                 
                <tabRow/>
                </tbody>
            </table>
        </div>
    </div>
    )
  }
}

const mapStateToProps = (state) => ({
  
})

const mapDispatchToProps =dispatch=>( {
  actions: bindActionCreators(AuthAction, dispatch)
})

export default connect(mapStateToProps, mapDispatchToProps)(Manager)
