import React, { Component } from 'react'
import axios from 'axios'
import TableRow from './TableRow'
import * as AuthAction from '../../actions/userActions';
import { connect } from 'react-redux'
import '../../styles/css/main.css';
import { Link, Router } from 'react-router-dom';
import history from '../../Config/history';
import { actFetchUsersRequest, actDeleteUsersRequest } from '../../actions/managerAction';
export class ManagerUser extends Component {
  constructor(props) {
    super(props)

    this.state = {
    }
  }
  componentDidMount() {
    this.props.fetchAllUsers();
  }
  onDelete=(id)=>{
    this.props.onDeleteUsers(id);
    history.push("/user")
  }

  render() {
    const { users } = this.props;
    return (
      <div>
        <div className="app-title">
          <div>
            <h1><i className="fa fa-user"></i>&nbsp; Quản lí người dùng</h1>
            <p></p>
          </div>
          <ul className="app-breadcrumb breadcrumb">
            <li className="breadcrumb-item"><i className="fa fa-home fa-lg"></i></li>
            <li className="breadcrumb-item"><a href="#">User</a></li>
          </ul>
        </div>

        <div className="row">
          <div className="col-lg-7">
            <p className="bs-component ">
              <a href="adduser.html">
                <Link className="btn btn-primary btn-right" to="/manager/user/add">+ Thêm</Link>
              </a>
            </p>
          </div>
          <div className="col-md-12">
            <div className="tile">
              <div className="tile-body">
                <table className="table table-hover table-bordered" id="sampleTable">
                  <thead>
                    <tr>
                      <th>STT</th>
                      <th>Email</th>
                      <th>Tên đầy đủ</th>
                      <th>Ngày sinh</th>
                      <th>Địa chỉ</th>
                      <th>ID</th>
                      <th>Thực hiện</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.tabRow(users)}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
  tabRow(users) {
    return users.map((object, i) => {
      return <TableRow obj={object} key={i} onDelete={this.onDelete}/>
    })
  }
}

const mapStateToProps = state => {
  return {
    users: state.users
  }
}

const mapDispatchToProps = (dispatch, props) => {
  return {
    fetchAllUsers: () => {
      dispatch(actFetchUsersRequest())
    },
    onDeleteUsers:(id)=>{
      dispatch(actDeleteUsersRequest(id))
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ManagerUser)
