import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux'

export class ManagerCategory extends Component {
  constructor(props) {
    super(props)
  }
  componentDidMount(){
 //   this.props.getCategory();

  }
  onDelete = (id) => {
    this.props.deleteCategory(id);

  }
  

  render() {
    return (
      <div>
      <div class="app-title">
      <div>
        <h1><i class="fa fa-book"></i>&nbsp; Quản lí chuyên mục</h1>
        <p></p>
      </div>
      <ul class="app-breadcrumb breadcrumb">
        <li class="breadcrumb-item"><i class="fa fa-home fa-lg"></i></li>
        <li class="breadcrumb-item"><a href="#">Chuyên mục</a></li>
      </ul>
    </div>

    <div class="row">
      <div class="col-lg-7">
          <p class="bs-component ">
            <a href="addcategory.html">
            <Link class="btn btn-primary btn-right" to="/manager/category/add">+ Thêm</Link>            
            </a>
          </p>
      </div>
      <div class="col-md-12">
        <div class="tile">
          <div class="tile-body">
            <table class="table table-hover table-bordered" id="sampleTable">
              <thead>
                <tr>
                  <th>STT</th>
                  <th>Tên chuyên mục</th>
                  <th>Ngày đăng</th>
                  <th>Người đăng</th>
                  <th>Thực hiện</th>
                </tr>
              </thead>
              <tbody>
              <Pagination
              currentPage={this.state.currentPage}
              totalPages={this.props.totalPages}
              changeCurrentPage={this.changeCurrentPage}
              theme="default"
            />
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    category: state.category
  }
}
const mapDispatchToProps = {
  
}

export default connect(mapStateToProps, mapDispatchToProps)(ManagerCategory)
