import React from 'react';

const TextField = ({
  input, meta: { touched, error }, isError,isEmailInvalid,isPassInvalid, ...restOfInput
}) => (
  <div>
  <input
    {...input}

    {...restOfInput}
  />
  {touched && error  && <span>{error}</span>}
  {!isEmailInvalid && <span>Email is invalid</span>}
  {!isPassInvalid && <span>Password must be 6-20 characters</span>}
  </div>
)

export default TextField
