import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import API from '../../../utils/api'
import { actAddUserRequest } from '../../../actions/managerAction';
class CreateUser extends Component {
  constructor(props) {
    super(props)

    this.state = {
      id: '',
      txtUsername: '',
      txtPassword: '',
      txtAddress: '',
      txtEmail: '',
      fileInput: '',
      txtPhone: '',
      txtBirthday: '',
      role: ''
    }
  }
  componentDidMount() {
    var { match } = this.props;
    if (match) {
      var id = match.params.id;
      API.get(`/users/${id}`).then(
        res => {
          var data = res.data;
          this.setState({
            id: data.id,
            txtUsername: data.username,
            txtPassword: data.password
          })
        }
      )
    }

  }
  onChange = (e) => {
    const target = e.target;
    const name = target.name;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value
    })
  }
  onSave = (e) => {
    e.preventDefault();
    const {txtEmail,txtPassword}= this.state;
    const user = {
      username: txtEmail,
      password: txtPassword
    }
    this.props.onAddUser(user);
    console.log(this.state)
  }

  render() {
    const { txtUsername, txtPassword, txtEmail, txtSkill, txtPhone, txtBirthday,
      fileInput, chkbGender, txtAddress, role } = this.state;
    return (

      <div className="row">
        <div className="col-md-12">
          <form onSubmit={this.onSave}>
            <div className="card-header">
              <strong>Sửa người dùng</strong>
            </div>
            <div className="tile">
              <div className="row">
                <div className="col-lg-6">

                  <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Email address</label>
                    <input
                      className="form-control"
                      id="exampleInputEmail1"
                      type="email"
                      aria-describedby="emailHelp"
                      placeholder="Enter email"
                      name="txtEmail"
                      value={txtEmail}
                      onChange={this.onChange}
                    />

                  </div>
                  <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Password</label>
                    <input
                      className="form-control"
                      id="exampleInputPassword1"
                      type="password"
                      placeholder="Password"
                      name="txtPassword"
                      value={txtPassword}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="exampleSelect1">Quyền</label>
                    <select className="form-control" id="exampleSelect1"
                      onChange={this.onChange} value={role} name="role"
                    >
                      <option selected="selected"></option>
                      <option name="User">User</option>
                      <option name="Members">Members</option>
                      <option name="Authors">Authors</option>
                      <option name="Manager">Manager</option>
                      <option name="Admin">Admin</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label htmlFor="exampleTextarea">Kỹ năng</label>
                    <textarea
                      className="form-control"
                      id="exampleTextarea"
                      rows={3}
                      name="txtSkill"
                      value={txtSkill}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="exampleInputFile">File input</label>
                    <input
                      className="form-control-file"
                      id="exampleInputFile"
                      type="file"
                      aria-describedby="fileHelp"
                      name="fileInput"
                      value={fileInput}
                      onChange={this.onChange}
                    />
                  </div>

                </div>
                <div className="col-lg-4 offset-lg-1">
                  <div className="form-group">
                    <label className="col-form-label" htmlFor="inputDefault">Họ Tên</label>
                    <input
                      className="form-control"
                      id="inputDefault"
                      type="text"
                      name="txtUsername"
                      value={txtUsername}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label className="col-form-label" htmlFor="inputDefault">Địa chỉ</label>
                    <input
                      className="form-control"
                      id="inputDefault"
                      type="text"
                      name="txtAddress"
                      value={txtAddress}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label className="col-form-label" htmlFor="inputDefault">Số điện thoại</label>
                    <input
                      className="form-control"
                      id="inputDefault"
                      type="text"
                      name={txtPhone}
                      value={txtPhone}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label className="control-label">Giới tính</label>
                    <div className="form-check">
                      <label className="form-check-label">
                        <input
                          className="form-check-input"
                          type="checkbox"
                          name="chkbGender"
                          value={chkbGender}
                          onChange={this.onChange} />
                        Nam
                    </label>
                    </div>

                  </div>
                  <div className="form-group">
                    <label className="col-form-label" htmlFor="inputDefault">Ngày sinh</label>
                    <input
                      type="date"
                      id="actor-input"
                      name="txtBirthday"
                      className="form-control"
                      value={txtBirthday}
                      onChange={this.onChange}

                    />
                  </div>
                </div>
              </div>
              <div className="tile-footer">

                <button
                  type="submit"
                  className="btn btn-primary btn-sm"
                >
                  Save
            </button>
                &nbsp;

              &nbsp;
              <Link to="/manager/user">
                  <button className="btn btn-danger btn-sm" type="button">Back</button>
                </Link>
              </div>

            </div>
          </form>
        </div>
      </div>

    )
  }
}


const mapDispatchToProps = (dispatch, props) => {
  return {
    onAddUser: (user) => {
      dispatch(actAddUserRequest(user));
    }
  }
}

export default connect(null, mapDispatchToProps)(CreateUser)
