import * as type from './../Config/actionConstant';
import callAPI from './../utils/CallAPI';
//import API from '../utils/api';
{/* luu data tu sever ve stogre*/ }

export const saveNewsToStore=(news)=>{
    return{
        type:type.GET_NEWS,
        news:news
    }
}
export const saveNewsRequest=()=>{
    return (dispath)=>{
        return callAPI('new','GET',null).then(reps=>{
            
            dispath(saveNewsToStore(reps.data));
        });
    };
}

{/* xoa data tren sever sau do tren stogre*/ }

export const deleteNewStore = (id) => {
    return {
        type: type.DELETE_NEW,
        id: id
    }
}
export const deleteNewRequest = (id) => {
    return (dispath) => {
        return callAPI(`new/${id}`, 'DELETE', null).then(reps => {
            dispath(deleteNewStore(id))
        });
    };
}
export const addNewsRequest=(news)=>{
    return(dispath)=>{
        return callAPI('new', 'POST', news).then(reps => {
            console.log(reps);
            if(reps.status===201)
            {
              
                alert('Them thanh cong');

            }
            //console.log(user);
            
            
        });
    };
}
