import * as Types from '../Config/managerConstant';
import API from '../utils/api';



export const actFetchUsersRequest=()=>{
    return (dispatch)=>{
        return API.get('/users').then(res=>{
            dispatch(actFetchUsers(res.data))
        })
    }
}

export const actFetchUsers= (users)=>{
    return{
        type:Types.FETCH_USERS,
        users
    }
}
export const actDeleteUsersRequest=(id)=>{
    return (dispatch)=>{
        return API.delete(`/users/${id}`).then(res=>{
            dispatch(actDeleteUsers(res.id))
        })
    }
}
export const actDeleteUsers=(id)=>{
    return{
        type:Types.DELETE_USER,
        id
    }
}
export const actAddUsersRequest=(user)=>{
    return dispatch=>{
        return API.post('/users/',{user})
    }
}
