import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import { saveNewsRequest, deleteNewRequest } from './../../actions/newsAction'
import TableNews from './tblNews'


export class ManagerNews extends Component {
  componentDidMount() {
    this.props.getNews();

  }
  onDelete = (id) => {
    this.props.deleteNew(id);
    console.log(id)

  }

  render() {
    var { news } = this.props;
    var elem = news.map((n, index) => {
      return <TableNews
        key={index}
        news={n}
        index={index}
        Delete={this.onDelete}
      />
    });
    return (
      <div>
         <div className="app-title">
          <div>
            <h1><i className="fa fa-user"></i>&nbsp; Quản lí tin tuc</h1>
            <p></p>
          </div>
          <ul className="app-breadcrumb breadcrumb">
            <li className="breadcrumb-item"><i className="fa fa-home fa-lg"></i></li>
            <li className="breadcrumb-item"><a>Tin tuc</a></li>
          </ul>
        </div>
        <div className="row">
          <div className="col-lg-7">
            <p className="bs-component ">
              <a href="addnews.html">
                <Link className="btn btn-primary btn-right" to="/manager/news/add">+ Thêm</Link>              </a>
            </p>
          </div>
          <div className="col-md-12">
            <div className="tile">
              <div className="tile-body">
                <table className="table table-hover table-bordered" id="sampleTable">
                  <thead>
                    <tr>
                      <th>STT</th>
                      <th>Tiêu đề</th>
                      <th>Chuyên mục</th>
                      <th>Hình ảnh</th>
                      <th>Ngày đăng</th>
                      <th>Người đăng</th>
                      <th>Nội dung vắn tắt</th>
                      <th>Thực hiện</th>
                    </tr>
                  </thead>
                  <tbody>
                    {elem}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    news: state.News
  }

}

const mapDispatchToProps = (dispath, props) => {
  return {
    getNews: () => {

      dispath(saveNewsRequest());
    },
    deleteNew: (id) => {
      dispath(deleteNewRequest(id));
    }
  }
};


export default connect(mapStateToProps, mapDispatchToProps)(ManagerNews)
