import React, { Component } from 'react'
import { connect } from 'react-redux'
import Pagination from '../../pagination/Pagination'
export class ManagerSources extends Component {
    constructor(props) {
        super(props)

        this.state = {
          currentPage:1
        }
    }
    componentDidMount(){
      this.props.actions.fetchSource(this.state.currentPage-1,5);
    }


    render() {
        return (
            <div>
            <div class="app-title">
            <div>
              <h1><i class="fa fa-address-card-o"></i>&nbsp; Quản lí nghề nghiệp</h1>
              <p></p>
            </div>
            <ul class="app-breadcrumb breadcrumb">
              <li class="breadcrumb-item"><i class="fa fa-home fa-lg"></i></li>
              <li class="breadcrumb-item"><a href="#">Hồ sơ</a></li>
            </ul>
          </div>

          <div class="row">
            <div class="col-lg-7">
                <p class="bs-component ">
                  <a href="addjob.html">
                    <button class="btn btn-primary btn-right" type="button">+ Thêm</button>
                  </a>
                </p>
            </div>
            <div class="col-md-12">
              <div class="tile">
                <div class="tile-body">
                  <table class="table table-hover table-bordered" id="sampleTable">
                    <thead>
                      <tr>
                        <th>STT</th>
                        <th>ID</th>
                        <th>Tên chuyên mục</th>
                        <th>Link</th>
                        <th>Cho phép</th>
                        <th>Thực hiện</th>
                      </tr>
                    </thead>
                    <tbody>

                    </tbody>
                  </table>
                  <Pagination
                  currentPage={this.state.currentPage}
                  totalPages={this.props.totalPages}
                  changeCurrentPage={this.changeCurrentPage}
                  theme="default"
                />
                </div>
              </div>
            </div>
          </div>
            </div>
        )
    }
}

