import React, { Component } from 'react'
import { connect } from 'react-redux'
import{Link} from 'react-router-dom'
export class ManagerNews extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
       currentPage:1,
       keyword:''
    }
  }
  tabRow(users,currentPage,userid) {
    return users.map((object, i) => {
      return <TableRowNews obj={object} key={i} index={i+(currentPage-1)*5} onDelete={this.onDelete} userid={userid} />
    })
  }
  

  render() {
    return (
      <div>
         <div className="app-title">
          <div>
            <h1><i className="fa fa-user"></i>&nbsp; Quản lí tin tức</h1>
            <p></p>
          </div>
          <ul className="app-breadcrumb breadcrumb">
            <li className="breadcrumb-item"><i className="fa fa-home fa-lg"></i></li>
            <li className="breadcrumb-item"><a>Tin tuc</a></li>
          </ul>
        </div>
        <div className="row">
          <div className="col-lg-7">
            <p className="bs-component ">
              <a href="addnews.html">
                <Link className="btn btn-primary btn-right" to="/manager/news/add">+ Thêm</Link>              </a>
            </p>
          </div>
          <div className="col-md-12">
            <div className="tile">
              <div className="tile-body">
                <table className="table table-hover table-bordered" id="sampleTable">
                  <thead>
                    <tr>
                      <th>STT</th>
                      <th>Tiêu đề</th>
                      <th>Chuyên mục</th>
                      <th>Hình ảnh</th>
                      <th>Ngày đăng</th>
                      <th>Cho phép</th>
                      <th>Thực hiện</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.tabRow(this.props.news,this.state.currentPage)}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

    )
  }
}

const mapStateToProps = (state) => ({

    news: state.news.news,
    totalPages: state.news.totalPages
  
})

const mapDispatchToProps =dispatch=> {
  return{
    fetchAllNews: (currentPage,size)=>{
      dispatchEvent(actFetchNewsRequest(currentPage,size))
    },
    onDeleteUsers: id=>{
      dispatchEvent(actDeleteNewsRequest(id))
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ManagerNews)
