import * as type from './../Config/actionConstant';
import callAPI from './../utils/CallAPI';
{/* luu data tu sever ve stogre*/ }

export const saveCategoryToStore=(category)=>{
    return{
        type:type.GET_CATEGORY,
        category:category
    }
}
export const saveCategoryRequest=()=>{
    return (dispath)=>{
        return callAPI('category','GET',null).then(reps=>{
        
            dispath(saveCategoryToStore(reps.data));
        });
    };
}
{/* xoa data tren sever sau do tren stogre*/ }

export const deleteCategoryStore = (id) => {
    return {
        type: type.DELETE_CATEGORY,
        id: id
    }
}
export const deleteCategoryRequest = (id) => {
    return (dispath) => {
        return callAPI(`category/${id}`, 'DELETE', null).then(reps => {
            dispath(deleteCategoryStore(id))
        });
    };
}
export const addCategoryRequest=(category)=>{
    return(dispath)=>{
        return callAPI('category', 'POST', category).then(reps => {
            console.log(reps);
            if(reps.status===201)
            {    
                alert('Them thanh cong');
            }
            //console.log(user) 
        });
    };
}
