import React, { Component } from 'react'
import { connect } from 'react-redux'
import { addCategoryRequest } from './../../../actions/categoryAction'
import API from './../../../utils/api'

export class editUser extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id:'',
            txtName:'',
            ckEnable:true,
            slcSource:'',
            txtLink:'',

        }
    }
    componentDidMount() {
      //  this.props.getCategory();
        var { match } = this.props;
        if (match) {
          var id = match.params.id;
          this.props.actions.onEditCate(id);
        }
      }
    componentWillReceiveProps(nextProps){
      if(nextProps && nextProps.itemEditing){
        const {itemEditing} = nextProps;
        this.setState({
          id: itemEditing.id,
          txtName:itemEditing.categoryName,
          ckEnable:itemEditing.categoryEnable,
          slcSource:itemEditing.categorySourceId,
          txtLink:itemEditing.categoryLink
        })
      }
    }
    onChange = (e) => {
        var target = e.target;
        var name = target.name;
        var value =  target.value;
        this.setState({
          [name]: value
        });

      }
    onSave=  async (e)=>{
      e.preventDefault();
      const {history}= this.props
      const {id, txtName , checkEnable,slcSource,txtLink}= this.state;
      const category={
        categoryName:txtName,
        categoryEnable:checkEnable,
        categorySourceId:slcSource,
        categoryLink:txtLink
      }
      if(id){
      this.props.actions.actUpdateCate(id,category)
      }else{
        this.props.actions.actAddCate(category)
      }
      history.goBack();
    }
    render() {
      const {id,txtName,txtLink,ckEnable,slcSource}=this.state
        return (
          <div className="row">
          <div className="col-md-12">
            <form onSubmit={this.onSave}>
              <div className="card-header">
                <strong></strong>
              </div>
              <div className="tile">

                <div className="row">
                  <div className="col-lg-6">
                    <div className="form-group">
                      <p value={id}></p>
                      <label htmlFor="exampleInputEmail1">Email address</label>
                      <input
                        className="form-control"
                        id="exampleInputEmail1"
                        type="email"
                        aria-describedby="emailHelp"
                        placeholder="Enter email"
                        name="txtEmail"
                        value={txtEmail}
                        onChange={this.onChange}
                        required
                      />

                    </div>


                    <div className="form-group">
                      <label htmlFor="exampleSelect1">Quyền</label>
                      <select className="form-control" id="exampleSelect1"
                        onChange={this.onChange} value={slcSource} name="role"
                      >
                        <option selected="selected"></option>
                        <option name="User">ROLE_USER</option>
                        <option name="Admin">ROLE_ADMIN</option>
                      </select>
                    </div>



                  </div>
                  <div className="col-lg-4 offset-lg-1">

                    <div className="form-group">
                      <label htmlFor="exampleInputName">Họ tên</label>
                      <input
                        className="form-control"
                        id="exampleInputPassword1"
                        type="text"
                        placeholder="Name"
                        name="txtName"
                        value={txtName}
                        onChange={this.onChange}
                      />
                    </div>

                    <div className="form-group">
                      <label className="col-form-label" htmlFor="inputDefault">Số điện thoại</label>
                      <input
                        className="form-control"
                        id="inputDefault"
                        type="text"
                        name="txtPhone"
                        value={txtPhone}
                        onChange={this.onChange}
                      />

                    </div>


                  </div>
                </div>
                <div className="tile-footer">

                  <button
                    type="submit"
                    className="btn btn-success btn-sm"
                  >
                    Save
              </button>
                  &nbsp;

                &nbsp;
                <Link to="/manager/user">
                    <button className="btn btn-danger btn-sm" type="button">Back</button>
                  </Link>
                </div>

              </div>
            </form>
          </div>
          </div>
        )
    }
}

const mapStateToProps = (state) => ({

})

const mapDispatchToProps = (dispath, props) => {
    return {
      addCategory: (category) => {

        dispath(addCategoryRequest(category));
      }

    }
  };

export default connect(mapStateToProps, mapDispatchToProps)(editUser)
