import React, { Component } from 'react';
import { Link } from 'react-router-dom';
class TableNews extends Component {
  Delete=(id)=>
  {
      //console.log(id);
      if(confirm('Ban chac muon xoa')){//eslint-disable-line
          this.props.Delete(id);
      }
      
  }
  render() {
    var { news, index } = this.props;

    return (

      <tr>
        <td>{index}</td>
        <td>{news.newsTitle}</td>
        <td>{news.tblCategory.categoryTitle}
        </td>
        <td>{news.id.newsId}</td>
        <td>{news.newsPostDay}</td>
        <td>{news.tblUser.userFullname}</td>
        <td>{news.newsContent}</td>
        <td>
          <Link
            className="btn btn-primary"
            to={`/manager/user/${news.id.newsId}/edit`}
          >
            Sá»­a </Link>
            <button type="button" 
                                className="btn  btn-warning"
                                onClick={()=>this.Delete(news.id.newsId)} 
                        >Xoa</button>

        </td>
      </tr>
    );
  }
}

export default TableNews;
