class Manager extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
          users:[],
         userId:'',
         userEmail:'',
         userPassword:'',
         userFirstname:'',
         userLastname:'',
         userMobile:'',
         userRole:'',
         userStatus:'',
      }
    }
    componentDidMount(){
        this.props.actions.getAllUsers=this.state.users
        
    }
    tabRow(){
        return this.state.users.map(function(object,i){
            return <TableRow obj={object} key={i}/>
        });
    }
  render() {
    return (
        <div className="managerContainer">
        <div>
            {/*user data */}
            <table className="tableContainer">
                <tbody>
                 
                {this.tabRow()}
                </tbody>
            </table>
        </div>
    </div>
    )
  }
}

