import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import Popup from 'reactjs-popup'
import { bindActionCreators } from 'redux';
import * as NewsAction from '../../actions/newsAction'
import TableRowNews from './TableRowNews'
import Pagination from '../../pagination/Pagination'
export class ManagerNews extends Component {
  constructor(props) {
    super(props)

    this.state = {
      currentPage: 1,
      keyword: ''
    }
  }
  componentDidMount() {
    this.props.actions.actFetchNewsRequest(this.state.currentPage - 1, 20)
  }

  onDelete = (id) => {
    this.props.actions.deleteNewsRequest(id);
  }
  changeCurrentPage = async (numPage) => {
    await this.setState({ currentPage: numPage });
    await this.props.actions.actFetchNewsRequest(numPage - 1, 20);
  }
  onHandleChange = (e) => {
    this.setState({
      keyword: e.target.value
    })
  }
  onCrawl = (id=1) => {
    this.props.actions.actCrawlNewsRequest(id)
  }

  onFindUser = (e) => {
    console.log(this.state.keyword)
    e.preventDefault();
    //   this.props.onFindUser(this.state.keyword);

  }
  tabRow(news, currentPage) {
    return news.map((object, i) => {
      return <TableRowNews news={object} key={i} index={i + (currentPage - 1) * 20} onDelete={this.onDelete} />
    })
  }


  render() {
    return (
      <div>
        <div className="app-title">
          <div>
            <h1><i className="fa fa-user"></i>&nbsp; Quản lí tin tức</h1>
            <p></p>
          </div>
          <ul className="app-breadcrumb breadcrumb">
            <li className="breadcrumb-item"><i className="fa fa-home fa-lg"></i></li>
            <li className="breadcrumb-item"><a>Tin tuc</a></li>
          </ul>
        </div>
        <div className="row">
          <div className="col-lg-7">
            <p className="bs-component ">
              <Popup trigger={<input className="btn btn-primary btn-right" type="button" value="Them" />} modal closeOnDocumentClick>
                {close => (
                  <div className="mod" style={{width:'100%',padding:'2em 1em'}}>
                    <a className="close" onClick={close}> &times;</a>
                    <div className="headerPopup">
                      Lựa chọn chuyên mục lấy tin
                  </div>
                    <div className="content">
                    <select onChange={this.onChange} value="cate" name="cate" >
                      <option value="18">Thời sự</option>
                      <option value="">Kinh tế</option>
                      <option value="1">Kinh doanh</option>
                      <option value="19">Thế giới</option>
                    </select>
                    </div>
                    <button
                      className="btn btn-primary"
                      onClick={this.onCrawl}
                    >
                    Lấy tin
                    </button>
                    <button
                      className="button"
                      onClick={() => {
                        
                        close()
                      }}
                    > close</button>
                  </div>
                )
                }
              </Popup>
            </p>
          </div>
          <div className="col-md-12">
            <div className="tile">
              <div className="tile-body">
                <table className="table table-hover table-bordered" id="sampleTable">
                  <thead>
                    <tr>
                      <th>STT</th>
                      <th>ID</th>
                      <th>Tiêu đề</th>
                      <th>Link</th>
                      <th>Chuyên mục</th>
                      <th>Hình ảnh</th>
                      <th>Ngày đăng</th>
                      <th>Cho phép</th>
                      <th>Thực hiện</th>
                    </tr>
                  </thead>
                  <tbody>

                    {this.tabRow(this.props.news, this.state.currentPage)}
                  </tbody>
                </table>
                <Pagination
                  currentPage={this.state.currentPage}
                  totalPages={this.props.totalPages}
                  changeCurrentPage={this.changeCurrentPage}
                  theme="default"
                />
              </div>
            </div>
          </div>
        </div>
      </div>

    )
  }
}


const mapStateToProps = (state) => ({

  news: state.news.news,
  totalPages: state.news.totalPages

})

const mapDispatchToProps = dispatch => ({

  // fetchAllNews: (currentPage, size) => {
  //   dispatchEvent(actFetchNewsRequest(currentPage, size))
  // },
  // onDeleteNews: id => {
  //   dispatchEvent(actDeleteNewsRequest(id))
  // },

  actions: bindActionCreators(NewsAction, dispatch)
})

export default connect(mapStateToProps, mapDispatchToProps)(ManagerNews)
