export default (values)=>{
    let emailPatt=(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i);

    const errors ={}
    if(!values.username){
        errors.username ='username required'
    }else{
        if(values.username.include("@")){
            let isEmailCorrect = emailPatt.test(values.username)
            if(isEmailCorrect){
                errors.username ='Invalid email'
            }
        }
    }
    if(!values.password){
        errors.password ='password required'
    }else{
        if(values.password.length<6 || values.password.length>20){
            errors.password='password must be 6-20 characters'
        }
    }
    return errors


}
