import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { Link } from 'react-router-dom';

import validate from './LoginFormValidation';
import TextField from '../elements/TextField';
import Label from '../elements/Label';
import Button from '../elements/Button';
import '../../styles/style/style.css';

const LoginForm = ({ handleSubmit, isLoginFailed,isEmailCorrect,isPasswordCorrect }) => (
    <form onSubmit={(e) => handleSubmit(e)}>
        <div class="col-12 col-md-6 col-lg-4" >
            <div className="newsletter-widget">
              
                            <h3 className="panel-title" style={{ textAlign: 'center' }}>Login</h3>
                            <fieldset>
                                <div className="form-group">
                                    <div className="input-group input-group-lg">
                                        <Label htmlFor="username">
                                            username
                                        </Label>
                                        <Field component={TextField}
                                            isError={isLoginFailed}
                                            name="username"
                                            type="text"
                                            className="form-control"
                                           
                                            
                                        />
                         { isEmailCorrect ? (<span></span>) : (<span>Email is invalid</span>)}
                                    </div>
                                </div>
                                <div className="form-group">
                                    <div className="input-group input-group-lg">
                                        <Label htmlFor="password">
                                            password
                                        </Label>
                                        <Field component={TextField}
                                            isError={isLoginFailed}
                                            name="password"
                                            type="password"
                                            className="form-control"
                                    
                                        />
                            { isPasswordCorrect ? (<span></span>) : (<span>Password must be at least 6 characters</span>)}
                                    </div>
                                </div>
                                
                                <input
                                    className="btn btn-lg btn-primary btn-block"
                                    type="submit"
                                   
                                    value="Login"
                                />
            {isLoginFailed ? <span style={{textAlign:'center',color:'red',fontWeight:'bold'}}>login fail</span> : <span></span>}
                            </fieldset>

                            <p className="m-b-0 m-t">
                                Not signed up? <Link to="/register">Sign up here</Link>.
                            </p>          
                            </div>
        </div>

    </form>


)
export default reduxForm({
    form: 'login',
    validate,

})(LoginForm)
