import React, { Component } from 'react'
import axios from 'axios'
import TableRow from './TableRow'
import * as AuthAction from '../../actions/userActions';
import { connect } from 'react-redux'
import '../../styles/css/main.css';
import { Link, Router } from 'react-router-dom';
import history from '../../Config/history';
import { actFetchUsersRequest } from '../../actions/managerAction';
export class ManagerUser extends Component {
  constructor(props) {
    super(props)

    this.state = {
      users: [],
    }
  }
  componentDidMount() {
    this.props.fetchAllUsers();
  }
  tabRow(users) {
    return this.state.users.map((object, i) => {
      return <TableRow obj={object} key={i} />
    })
  }

  render() {
    const { users } = this.props;
    return (
      <div>
        <div class="app-title">
          <div>
            <h1><i class="fa fa-user"></i>&nbsp; Quản lí người dùng</h1>
            <p></p>
          </div>
          <ul class="app-breadcrumb breadcrumb">
            <li class="breadcrumb-item"><i class="fa fa-home fa-lg"></i></li>
            <li class="breadcrumb-item"><a href="#">User</a></li>
          </ul>
        </div>

        <div class="row">
          <div class="col-lg-7">
            <p class="bs-component ">
              <a href="adduser.html">
                <Link class="btn btn-primary btn-right" to="/manager/user/add">+ Thêm</Link>
              </a>
            </p>
          </div>
          <div class="col-md-12">
            <div class="tile">
              <div class="tile-body">
                <table class="table table-hover table-bordered" id="sampleTable">
                  <thead>
                    <tr>
                      <th>STT</th>
                      <th>Email</th>
                      <th>Tên đầy đủ</th>
                      <th>Ngày sinh</th>
                      <th>Địa chỉ</th>
                      <th>ID</th>
                      <th>Thực hiện</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.tabRow(users)}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return{
  users: state.users
}}

const mapDispatchToProps = (dispatch, props) => {
  return {
    fetchAllUsers: () => {
      dispatch(actFetchUsersRequest())
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ManagerUser)
