import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import { saveCategoryRequest, deleteCategoryRequest } from './../../actions/categoryAction'
import TableCategory from './tblCategory'

export class ManagerCategory extends Component {

  componentDidMount() {
    //this.props.getCategory();

  }
  onDelete = (id) => {
    this.props.deleteCategory(id);

  }


  render() {
    var { category } = this.props;
    var elem = category.map((n, index) => {
      return <TableCategory
        key={index}
        category={n}
        index={index}
        Delete={this.onDelete}
      />
    });
    return (
      <div>
      <div class="app-title">
      <div>
        <h1><i class="fa fa-book"></i>&nbsp; Quản lí chuyên mục</h1>
        <p></p>
      </div>
      <ul class="app-breadcrumb breadcrumb">
        <li class="breadcrumb-item"><i class="fa fa-home fa-lg"></i></li>
        <li class="breadcrumb-item"><a>Chuyên mục</a></li>
      </ul>
    </div>

    <div class="row">
      <div class="col-lg-7">
          <p class="bs-component ">
            <a href="addcategory.html">
            <Link class="btn btn-primary btn-right" to="/manager/category/add">+ Thêm</Link>            
            </a>
          </p>
      </div>
      <div class="col-md-9">
        <div class="tile">
          <div class="tile-body">
            <table class="table table-hover table-bordered" id="sampleTable">
              <thead>
                <tr>
                  <th>STT</th>
                  <th>Tên chuyên mục</th>
                  <th>Thực hiện</th>
                </tr>
              </thead>
              <tbody>
               {elem}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    category: state.category
  }

}

const mapDispatchToProps = (dispath, props) => {
  return {
    getCategory: () => {

      dispath(saveCategoryRequest());
    },
    deleteCategory: (id) => {
      dispath(deleteCategoryRequest(id));
    }
  }
};



export default connect(mapStateToProps, mapDispatchToProps)(ManagerCategory)
