import React, { Component } from 'react'
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import * as NewsAction from '../../actions/newsAction'
import Modal from 'react-responsive-modal';
import chain from '../../styles/style/img/core-img/chain.png'
import "moment/locale/vi"
import Moment from 'moment';

class Slide extends Component {
    constructor(props) {
        super(props)

        this.state = {
            open:false
        }
    }



    componentDidMount() {
        this.props.actions.actGetSlice();

    }
    tabRow = (news) => {
        return news.map((newz, i) => {
            return <SliceTable newz={newz} key={i} />
        })
    }
    onOpenPopup = () => {
        this.setState({ open: true })
    }
    onClosePopup = () => {
        this.setState({ open: false })
    }

    render() {
        const { news } = this.props

        return (
            <div class="single-welcome-slide">
                <div class="row no-gutters">
                    <div class="col-12 col-lg-8">
                        <div class="welcome-post">
                            <img src="" alt="" />
                            <div class="post-content" data-animation="fadeInUp" data-duration="500ms">
                                <a href="#" class="tag">Travel</a>
                                <a href="#" class="post-title">10 Tips to travel in style for less</a>
                                <p>1 day ago</p>
                            </div>
                        </div>
                    </div>

                    <div class="col-12 col-lg-4">
                        {this.tabRow(news)}
                    </div>
                </div>
            </div>
        )
    }
}

const style = {
    margin: '0 auto',
    padding: '10px',
    width: '100%',
    textAlign: 'justify',
    fontColor: '#000',
    fontFamily: "Bookman, Tahoma, Verdana",

}
const modals = {
    modal: {
        borderRadius: '9px',
        width: '700px',
    }

}

const mapStateToProps = state => ({
    news: state.news.slice
})
const mapDispatchToProps = dispatch => ({
    actions: bindActionCreators(NewsAction, dispatch)
})
export default connect(mapStateToProps, mapDispatchToProps)(Slide)
